/* Magnetic Buttons - Botones que siguen el cursor */
.magnetic {
  transition: transform 0.2s var(--ease-out);
}

/* Icon Spin - Rotación completa al hover */
.icon-spin-hover:hover .icon {
  animation: iconSpin 0.6s var(--ease-out);
}

/* Bounce Effect - Rebote sutil al hover */
.bounce-hover {
  transition: transform var(--duration-fast) var(--ease-bounce);
}

/* Text Underline - Subrayado animado */
.underline-animated {
  position: relative;
  display: inline-block;
}

rules.declarations:
content: '';
  position: absolute;
  width: 100%;
  height: 2px;
  bottom: -2px;
  left: 0;
  background: currentColor;
  transform: scaleX(0);
  transform-origin: right;
  transition: transform var(--duration-normal) var(--ease-out);

rules.fullRule:
.underline-animated::after {
  content: '';
  position: absolute;
  width: 100%;
  height: 2px;
  bottom: -2px;
  left: 0;
  background: currentColor;
  transform: scaleX(0);
  transform-origin: right;
  transition: transform var(--duration-normal) var(--ease-out);
}

/* Card Flip - Efecto de volteo 3D */
.card-flip {
  perspective: 1000px;
  transform-style: preserve-3d;
}

rules.declarations:
position: relative;
