rules.fullRule:
.progress-ring-circle {
  transform: rotate(-90deg);
  transform-origin: 50% 50%;
  transition: stroke-dashoffset var(--duration-slow) var(--ease-out);
}

/* Counter Animation - Animación de números */
.counter-animated {
  display: inline-block;
  transition: all var(--duration-normal) var(--ease-out);
}

/* Glow Effect - Efecto de brillo pulsante */
.glow-pulse {
  animation: glowPulse 2s ease-in-out infinite;
}

/* Shimmer Effect - Efecto de brillo deslizante */
.shimmer-effect {
  position: relative;
  overflow: hidden;
}

rules.declarations:
content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    90deg,
    transparent 0%,
    rgba(255, 255, 255, 0.1) 50%,
    transparent 100%
  );
  animation: shimmerSlide 2s infinite;

rules.fullRule:
.shimmer-effect::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    90deg,
    transparent 0%,
    rgba(255, 255, 255, 0.1) 50%,
    transparent 100%
  );
  animation: shimmerSlide 2s infinite;
}

/* Morphing - Efecto de transformación morfa */
.morph-icon {
  transition: all var(--duration-normal) var(--ease-bounce);
}

/* Elastic Button - Botón con efecto elástico */
.elastic-button {
  transition: transform var(--duration-fast) var(--ease-spring);
}

.elastic-button:active {
  transform: scale(0.95);
  transition: transform var(--duration-fast) var(--ease-in);
}

/* Scale on Hover - Escala suave */
.scale-hover {
  transition: transform var(--duration-fast) var(--ease-out);
}

/* Rotate on Hover - Rotación suave */
.rotate-hover {
  transition: transform var(--duration-normal) var(--ease-out);
}

/* Fade Scale on Hover - Desvanecimiento y escala */
.fade-scale-hover {
  transition: all var(--duration-normal) var(--ease-out);
}

/* Slide Up Reveal - Revelación deslizante */
.slide-up-reveal {
  position: relative;
  overflow: hidden;
}

rules.declarations:
content: attr(data-reveal);
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  padding: var(--space-2);
  background: var(--color-bg-elevated);
  transform: translateY(100%);
  transition: transform var(--duration-normal) var(--ease-out);

rules.fullRule:
.slide-up-reveal::after {
  content: attr(data-reveal);
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  padding: var(--space-2);
  background: var(--color-bg-elevated);
  transform: translateY(100%);
  transition: transform var(--duration-normal) var(--ease-out);
}

/* Focus Glow - Brillo al focus */
.focus-glow:focus {
  outline: none;
  box-shadow: 0 0 0 3px rgba(88, 166, 255, 0.3),
              0 0 20px rgba(88, 166, 255, 0.2);
}

/* Pulse Border - Borde pulsante */
.pulse-border {
  position: relative;
}

rules.declarations:
content: '';
  position: absolute;
  inset: -2px;
  border-radius: inherit;
  background: linear-gradient(45deg, var(--color-brand-primary), var(--accent-cyan), var(--color-brand-primary));
  background-size: 200% 200%;
  animation: pulseBorder 3s ease infinite;
