  --font-size-2xl: 1.25rem;   /* 20px - Para t铆tulos medianos */
  --font-size-3xl: 1.5rem;    /* 24px - Para t铆tulos grandes */
  --font-size-4xl: 1.75rem;   /* 28px - Para display */
  
  /* Pesos - Variaciones de Inter */
  --font-weight-normal: 400;
