/* ===========================================
    SISTEMA DE TOOLTIPS (Fase 3)
   =========================================== */

/* Contenedor del tooltip */
.tooltip-container

position: fixed;
  z-index: var(--z-tooltip);
  background: var(--color-bg-elevated);
  border: 1px solid var(--color-border-default);
  border-radius: var(--radius-md);
  padding: var(--space-2) var(--space-3);
  max-width: 280px;
  font-size: var(--font-size-sm);
  line-height: var(--line-height-normal);
  color: var(--color-text-primary);
  box-shadow: var(--shadow-lg);
  opacity: 0;
  pointer-events: none;
  transform: scale(0.95);
  transition: opacity var(--duration-normal) var(--ease-out),
              transform var(--duration-normal) var(--ease-out);
}

/* Tooltip visible */
.tooltip-container.tooltip-visible

opacity: 1;
  pointer-events: auto;
  transform: scale(1);
}
/* Flecha indicadora */
.tooltip-arrow

position: absolute;
  width: 8px;
  height: 8px;
  background: var(--color-bg-elevated);
  border: 1px solid var(--color-border-default);
  z-index: 0;
}
.tooltip-container.tooltip-right .tooltip-arrow

left: -5px;
  top: 50%;
  transform: translateY(-50%) rotate(45deg);
  border-top: none;
  border-right: none;
}
.tooltip-container.tooltip-left .tooltip-arrow

right: -5px;
  top: 50%;
  transform: translateY(-50%) rotate(45deg);
  border-bottom: none;
  border-left: none;
}

/* Animaci贸n de entrada para tooltip */
@keyframes tooltipFadeIn

opacity: 1;
    transform: scale(1) translateY(0);
  }
}

/* Animaci贸n de salida para tooltip */
@keyframes tooltipFadeOut

opacity: 0;
    transform: scale(0.95) translateY(-4px);
  }
}

/* Tooltip con animaci贸n de entrada */
.tooltip-container.tooltip-visible

animation: tooltipFadeIn var(--duration-normal) var(--ease-out);
}

