/* === COLORES BASE (GitHub Dark Style) === */
  --color-bg-primary: #0d1117;
  --color-bg-secondary: #161b22;
  --color-bg-tertiary: #21262d;
  --color-bg-elevated: #30363d;
  --color-bg-canvas: #010409;

  /* Gradientes de fondo */
  --bg-primary: linear-gradient(135deg, #0d1117 0%, #161b22 100%);
  --bg-secondary: linear-gradient(135deg, #161b22 0%, #21262d 100%);
  --bg-tertiary: #21262d;
  --bg-dark: #0d1117;
  --bg-darker: #010409;

  /* === COLORES DE MARCA === */
  --color-brand-primary: #58a6ff;
  --color-brand-secondary: #79c0ff;
  --primary-blue: #58a6ff;
  --primary-blue-dark: #1f6feb;
  --primary-blue-light: #79c0ff;
  --accent-cyan: #56d4dd;
  --accent-purple: #bc8cff;

  /* === COLORES SEMNTICOS === */
  --color-success: #3fb950;
  --color-success-muted: #238636;
  --color-success-subtle: rgba(63, 185, 80, 0.15);
  --color-danger: #f85149;
