  --color-danger-subtle: rgba(248, 81, 73, 0.15);
  --color-warning: #d29922;
  --color-warning-muted: #9e6a03;
  --color-warning-subtle: rgba(210, 153, 34, 0.15);
  --color-info: #58a6ff;

  /* Compatibilidad con nombres anteriores */
  --profit-green: #3fb950;
  --profit-green-dark: #238636;
  --profit-green-light: #56d364;
  --profit-green-glow: rgba(63, 185, 80, 0.4);
  --loss-red: #f85149;
  --loss-red-dark: #da3633;
  --loss-red-glow: rgba(248, 81, 73, 0.4);
  --success: #3fb950;
  --warning: #d29922;
  --error: #f85149;
  --info: #58a6ff;

  /* === COLORES DE TEXTO === */
  --color-text-primary: #f0f6fc;
  --color-text-secondary: #8b949e;
  --color-text-muted: #8b949e; /* Mejorado contraste WCAG AA - Era #6e7681 (3.2:1) */
  --color-text-inverse: #0d1117;
  --text-primary: #f0f6fc;
  --text-secondary: #8b949e;
  --text-muted: #6e7681;
  --text-dim: #484f58;

  /* === COLORES DE BORDE === */
  --color-border-default: #30363d;
  --color-border-muted: #21262d;
  --color-border-emphasis: #8b949e;

  /* === TIPOGRAFA === */
  --font-size-xs: 0.75rem; /* 12px - Aumentado desde 0.6875rem (11px) */
  --font-size-sm: 0.8125rem; /* 13px - Aumentado desde 0.75rem (12px) */
  --font-size-base: 0.875rem; /* 14px - Aumentado desde 0.8125rem (13px) */
  --font-size-md: 0.875rem; /* 14px */
  --font-size-lg: 1rem; /* 16px */
  --font-size-xl: 1.125rem; /* 18px */
  --font-size-2xl: 1.25rem; /* 20px */
  --font-weight-normal: 400;
  --font-weight-medium: 500;
  --font-weight-semibold: 600;
  --font-weight-bold: 700;
  --line-height-tight: 1.25;
  --line-height-normal: 1.5;
  --line-height-relaxed: 1.75;

  /* === ESPACIADO (Base 4px) === */
  --space-0: 0;
  --space-1: 0.25rem; /* 4px */
  --space-2: 0.5rem; /* 8px */
  --space-3: 0.75rem; /* 12px */
  --space-4: 1rem; /* 16px */
  --space-5: 1.25rem; /* 20px */
  --space-6: 1.5rem; /* 24px */
  --space-8: 2rem; /* 32px */
  /* Compatibilidad */
  --spacing-xs: 4px;
  --spacing-sm: 8px;
  --spacing-md: 12px;
  --spacing-lg: 16px;
  --spacing-xl: 20px;
  --spacing-2xl: 24px;
  --spacing-3xl: 32px;

  /* === BORDES === */
  --radius-sm: 4px;
  --radius-md: 6px;
  --radius-lg: 8px;
  --radius-xl: 12px;
  --radius-2xl: 16px;
  --radius-full: 9999px;
  /* Compatibilidad */
  --border-radius-sm: 6px;
  --border-radius-md: 8px;
  --border-radius-lg: 12px;
  --border-radius-xl: 16px;
  --border-radius: 8px;

  /* === SOMBRAS === */
  --shadow-sm: 0 1px 2px rgba(0, 0, 0, 0.3);
  --shadow-md: 0 4px 8px rgba(0, 0, 0, 0.4);
  --shadow-lg: 0 8px 16px rgba(0, 0, 0, 0.5);
  --shadow-xl: 0 16px 32px rgba(0, 0, 0, 0.6);
  --shadow-glow-success: 0 0 12px rgba(63, 185, 80, 0.4);
  --shadow-glow-danger: 0 0 12px rgba(248, 81, 73, 0.4);
  --shadow-glow-green: 0 0 16px var(--profit-green-glow);
  --shadow-glow-red: 0 0 16px var(--loss-red-glow);

  /* === ANIMACIONES === */
  --duration-instant: 75ms;
  --duration-fast: 150ms;
  --duration-normal: 250ms;
  --duration-slow: 350ms;
  --ease-default: cubic-bezier(0.4, 0, 0.2, 1);
  --ease-in: cubic-bezier(0.4, 0, 1, 1);
  --ease-out: cubic-bezier(0, 0, 0.2, 1);
  --ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);
  /* Compatibilidad */
  --anim-duration-fast: 0.15s;
  --anim-duration-normal: 0.25s;
  --anim-duration-slow: 0.35s;
  --anim-easing: cubic-bezier(0.4, 0, 0.2, 1);
  --anim-easing-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);

  /* === DURACIONES DE ANIMACIN MEJORADAS (Fase 1) === */
  --duration-instant: 50ms;
  --duration-fast: 100ms;
  --duration-normal: 150ms;
  --duration-slow: 200ms;
  --duration-slower: 300ms;

  /* === EASING FUNCTIONS MEJORADAS (Fase 1) === */
  --ease-default: cubic-bezier(0.4, 0, 0.2, 1);
  --ease-in: cubic-bezier(0.4, 0, 1, 1);
  --ease-out: cubic-bezier(0, 0, 0.2, 1);
  --ease-in-out: cubic-bezier(0.4, 0, 0.2, 1);
  --ease-bounce: cubic-bezier(0.68, -0.55, 0.265, 1.55);

  /* === EASING FUNCTIONS ADICIONALES (Fase 1 Fundamentos UI/UX) === */
  --ease-out-quart: cubic-bezier(0.25, 1, 0.5, 1);
  --ease-out-expo: cubic-bezier(0.16, 1, 0.3, 1);
  --ease-in-out-quart: cubic-bezier(0.77, 0, 0.175, 1);
  --ease-spring: cubic-bezier(0.34, 1.56, 0.64, 1);
  --ease-smooth: cubic-bezier(0.4, 0, 0.2, 1);

  /* === Z-INDEX SCALE (Fase 1 Fundamentos UI/UX) === */
  --z-base: 1;
  --z-dropdown: 100;
  --z-sticky: 200;
  --z-fixed: 300;
  --z-modal-backdrop: 400;
  --z-modal: 500;
  --z-popover: 600;
  --z-tooltip: 700;

  /* === INPUTS === */
  --input-bg: rgba(13, 17, 23, 0.8);
  --input-border: var(--color-border-default);
  --input-border-focus: var(--primary-blue);
  --input-shadow-focus: 0 0 0 3px rgba(88, 166, 255, 0.2);
  --input-radius: var(--radius-md);
  --input-padding: 8px 12px;
  --input-transition: all var(--duration-fast) var(--ease-default);

  /* === CARDS === */
  --card-bg: linear-gradient(135deg, rgba(22, 27, 34, 0.9) 0%, rgba(33, 38, 45, 0.7) 100%);
  --card-bg-hover: linear-gradient(135deg, rgba(22, 27, 34, 1) 0%, rgba(33, 38, 45, 0.9) 100%);
  --card-border: 1px solid var(--color-border-default);
  --card-border-hover: 1px solid var(--color-border-emphasis);
  --shadow-card: var(--shadow-md);
  --shadow-card-hover: var(--shadow-lg);
  --border-card: 1px solid rgba(48, 54, 61, 0.8);
  --border-card-hover: 1px solid rgba(88, 166, 255, 0.4);
  --backdrop-blur: blur(12px);
  --transition-fast: all var(--duration-fast) var(--ease-default);
  --hover-lift: translateY(-2px);

  /* === INDICADORES DE PROFIT === */
  --indicator-width: 3px;
  --indicator-width-high: 4px;
  --indicator-opacity: 0.9;
  --indicator-opacity-hover: 1;
  --profit-positive: var(--color-success);
  --profit-negative: var(--color-danger);
  --profit-high: var(--color-success);
  --profit-low: var(--color-warning);
  --border-hover: var(--color-border-emphasis);

  /* === GRADIENTES === */
  --gradient-primary: linear-gradient(135deg, #1f6feb 0%, #58a6ff 100%);
  --gradient-primary-hover: linear-gradient(135deg, #58a6ff 0%, #79c0ff 100%);
  --gradient-success: linear-gradient(135deg, #238636 0%, #3fb950 100%);
  --gradient-success-hover: linear-gradient(135deg, #3fb950 0%, #56d364 100%);
  --gradient-warning: linear-gradient(135deg, #9e6a03 0%, #d29922 100%);
  --gradient-error: linear-gradient(135deg, #da3633 0%, #f85149 100%);
  --gradient-info: linear-gradient(135deg, #1f6feb 0%, #58a6ff 100%);
  --gradient-info-hover: linear-gradient(135deg, #388bfd 0%, #58a6ff 100%);
  --gradient-neutral: linear-gradient(135deg, #6e7681 0%, #484f58 100%);
  --gradient-neutral-hover: linear-gradient(135deg, #484f58 0%, #30363d 100%);
  --gradient-profit: linear-gradient(180deg, #3fb950 0%, #238636 100%);
  --gradient-profit-high: linear-gradient(180deg, #56d364 0%, #3fb950 100%);
  --gradient-loss: linear-gradient(180deg, #f85149 0%, #da3633 100%);
  --gradient-bg-dark: linear-gradient(180deg, #0d1117 0%, #161b22 100%);
  --gradient-bg-darker: linear-gradient(180deg, #010409 0%, #0d1117 100%);
}

/* ============================================
   ACCESIBILIDAD GLOBAL v6.0.0
   Focus states consistentes para todos los elementos
   ============================================ */

/* Focus visible para navegaci贸n por teclado */
:focus

outline: none;
}

/* Focus visible mejorado con transici贸n suave (Fase 1) */
:focus-visible

outline: 2px solid var(--color-brand-primary);
  outline-offset: 2px;
  transition: outline-color var(--duration-fast) var(--ease-out);
}

/* Focus para botones */
button:focus-visible,
.btn:focus-visible,
.btn-settings:focus-visible,
.btn-refresh:focus-visible,
.btn-configure:focus-visible,
.btn-recalculate:focus-visible

outline: 2px solid var(--color-brand-primary);
  outline-offset: 2px;
  box-shadow: 0 0 0 4px rgba(88, 166, 255, 0.2);
}

/* Focus para tabs */
.tab:focus-visible,
.tab-button:focus-visible,
.banks-tab-btn:focus-visible

outline: 2px solid var(--color-brand-primary);
  outline-offset: -2px;
  box-shadow: inset 0 0 0 2px rgba(88, 166, 255, 0.3);
}

/* Focus para inputs */
input:focus-visible,
select:focus-visible,
textarea:focus-visible

border-color: var(--color-brand-primary);
  box-shadow: 0 0 0 3px rgba(88, 166, 255, 0.2);
}

/* Focus para links */
a:focus-visible

outline: 2px solid var(--color-brand-primary);
  outline-offset: 2px;
  border-radius: 2px;
}

