/* ==========================================
    ANIMACIONES CONSOLIDADAS
   ========================================== */

/* Pulse - Animaci贸n universal para indicadores activos */
@keyframes pulse

opacity: 0.7;
    transform: scale(1.05);
  }
}

/* Pulse Glow - Con efecto de brillo */
@keyframes pulseGlow

box-shadow:
      0 0 15px currentColor,
      0 0 30px currentColor;
  }
}

/* Skeleton Loader - Para estados de carga */
@keyframes skeletonPulse

background-position: -200% 0;
  }
}

/* Flash Update - Para datos actualizados */
@keyframes flashUpdate

background: transparent;
    opacity: 1;
  }
}

/* Tab Enter - Transici贸n de pesta帽as */
@keyframes tabEnter

opacity: 1;
    transform: translateY(0);
  }
}

/* Fade In */
@keyframes fadeIn

opacity: 1;
    transform: translateY(0);
  }
}

/* Rotate - Para spinners */
@keyframes rotate

transform: rotate(360deg);
  }
}

/* Shake - Para errores */
@keyframes shake

transform: translateX(5px);
  }
}

/* Slide Down - Para elementos que aparecen desde arriba */
@keyframes slideDown

opacity: 1;
    transform: translateY(0);
  }
}

/* Step Slide In - Para pasos de gu铆a */
@keyframes stepSlideIn

opacity: 1;
    transform: translateX(0);
  }
}

/* ==========================================
   FASE 4: ANIMACIONES BASE - ENTRADA
   ========================================== */

/* Slide Up - Aparece desde abajo */
@keyframes slideUp

transform: translateY(0);
    opacity: 1;
  }
}

/* Scale In - Aparece con escala */
@keyframes scaleIn

transform: scale(1);
    opacity: 1;
  }
}

/* Stagger In - Para listas con delay */
@keyframes staggerIn

animation: scaleIn var(--duration-normal) var(--ease-spring);
  animation-fill-mode: both;
}

.stagger-in

animation: staggerIn var(--duration-normal) var(--ease-out-expo);
  animation-fill-mode: both;
}

/* Fade In mejorado */
.fade-in

animation: fadeIn var(--duration-normal) var(--ease-out-quart);
  animation-fill-mode: both;
}
/* ==========================================
   FASE 4: ANIMACIONES BASE - SALIDA
   ========================================== */

/* Fade Out - Desaparece desvaneciendo */
@keyframes fadeOut

opacity: 0;
  }
}

/* Slide Up Out - Desaparece hacia arriba */
@keyframes slideUpOut

transform: translateY(-20px);
    opacity: 0;
  }
}

/* Scale Out - Desaparece con escala */
@keyframes scaleOut

animation: slideUpOut var(--duration-fast) var(--ease-in);
  animation-fill-mode: forwards;
}
/* Skeleton loader base - Fase 2: Shimmer mejorado */
