/* ==========================================
   FASE 5: MICRO-INTERACCIONES AVANZADAS
   Efectos hover y animaciones mejoradas
   ========================================== */

/* Ripple Effect - Onda expansiva al hacer click */
.ripple

content: '';
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  pointer-events: none;
  background-image: radial-gradient(circle, rgba(255, 255, 255, 0.3) 10%, transparent 10.01%);
  background-repeat: no-repeat;
  background-position: 50%;
  transform: scale(10, 10);
  opacity: 0;
  transition: transform 0.4s var(--ease-out), opacity 0.4s var(--ease-out);
}

.ripple:active::after

transform: scale(1.02);
}

/* Icon Rotation - Rotaci贸n de iconos al hover */
.icon-rotate

transform: rotate(15deg);
}
/* Icon Spin - Rotaci贸n completa al hover */
.icon-spin-hover:hover .icon

content: '';
  position: absolute;
  width: 100%;
  height: 2px;
  bottom: -2px;
  left: 0;
  background: currentColor;
  transform: scaleX(0);
  transform-origin: right;
  transition: transform var(--duration-normal) var(--ease-out);
}
/* Card Flip - Efecto de volteo 3D */
.card-flip

transform: rotateY(180deg);
}
/* Progress Ring - Anillo de progreso circular */
.progress-ring

position: relative;
  display: inline-flex;
  align-items: center;
  justify-content: center;
}
/* Counter Animation - Animaci贸n de n煤meros */
.counter-animated

display: inline-block;
  transition: all var(--duration-normal) var(--ease-out);
}
@keyframes counterPulse

content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    90deg,
    transparent 0%,
    rgba(255, 255, 255, 0.1) 50%,
    transparent 100%
  );
  animation: shimmerSlide 2s infinite;
}

@keyframes shimmerSlide

transform: scale(1.05);
}
/* Scale on Hover - Escala suave */
.scale-hover

transition: transform var(--duration-fast) var(--ease-out);
}
/* Rotate on Hover - Rotaci贸n suave */
.rotate-hover

transition: transform var(--duration-normal) var(--ease-out);
}
/* Fade Scale on Hover - Desvanecimiento y escala */
.fade-scale-hover

transition: all var(--duration-normal) var(--ease-out);
}
/* Slide Up Reveal - Revelaci贸n deslizante */
.slide-up-reveal

transform: translateY(0);
}
/* Pulse Border - Borde pulsante */
.pulse-border

content: '';
  position: absolute;
  width: 200%;
  height: 200%;
  top: -50%;
  left: -50%;
  background: radial-gradient(circle, rgba(255, 255, 255, 0.1) 0%, transparent 70%);
  opacity: 0;
  transition: opacity var(--duration-normal) var(--ease-out);
}
.wave-effect:hover::after

content: '';
  position: absolute;
  width: 100px;
  height: 100px;
  background: radial-gradient(circle, rgba(255, 255, 255, 0.15) 0%, transparent 70%);
  border-radius: 50%;
  transform: translate(-50%, -50%);
  pointer-events: none;
  opacity: 0;
  transition: opacity var(--duration-fast) var(--ease-out);
}
/* Gradient Shift - Cambio de gradiente */
.gradient-shift

background-size: 200% 200%;
  animation: gradientShift 3s ease infinite;
}

@keyframes gradientShift

transform: perspective(1000px) rotateX(5deg) rotateY(5deg);
}

/* Accesibilidad: Respetar prefers-reduced-motion */
@media (prefers-reduced-motion: reduce)

animation: none !important;
    transition: none !important;
    transform: none !important;
  }
}

