/* ============================================
   ACCESIBILIDAD GLOBAL v6.0.0
   Focus states consistentes para todos los elementos
   ============================================ */

/* Focus visible para navegaci贸n por teclado */
:focus

outline: 2px solid var(--color-brand-primary);
  outline-offset: 2px;
  transition: outline-color var(--duration-fast) var(--ease-out);
}

/* Focus para botones */
button:focus-visible,
.btn:focus-visible,
.btn-settings:focus-visible,
.btn-refresh:focus-visible,
.btn-configure:focus-visible,
.btn-recalculate:focus-visible

outline: 2px solid var(--color-brand-primary);
  outline-offset: 2px;
  box-shadow: 0 0 0 4px rgba(88, 166, 255, 0.2);
}

/* Focus para tabs */
.tab:focus-visible,
.tab-button:focus-visible,
.banks-tab-btn:focus-visible

outline: 2px solid var(--color-brand-primary);
  outline-offset: -2px;
  box-shadow: inset 0 0 0 2px rgba(88, 166, 255, 0.3);
}

/* Focus para inputs */
input:focus-visible,
select:focus-visible,
textarea:focus-visible

border-color: var(--color-brand-primary);
  box-shadow: 0 0 0 3px rgba(88, 166, 255, 0.2);
}

/* Focus para links */
a:focus-visible

outline: 2px solid var(--color-brand-primary);
  outline-offset: 2px;
  border-radius: 2px;
}

/* Reducir movimiento para usuarios que lo prefieren */
@media (prefers-reduced-motion: reduce)

animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }
}

/* ==========================================
    ANIMACIONES CONSOLIDADAS
   ========================================== */

/* Pulse - Animaci贸n universal para indicadores activos */
@keyframes pulse

opacity: 0.7;
    transform: scale(1.05);
  }
}

/* Pulse Glow - Con efecto de brillo */
@keyframes pulseGlow

box-shadow:
      0 0 15px currentColor,
      0 0 30px currentColor;
  }
}

/* Skeleton Loader - Para estados de carga */
@keyframes skeletonPulse

background-position: -200% 0;
  }
}

/* Flash Update - Para datos actualizados */
@keyframes flashUpdate

background: transparent;
    opacity: 1;
  }
}

/* Tab Enter - Transici贸n de pesta帽as */
@keyframes tabEnter

transform: translateX(5px);
  }
}

/* Slide Down - Para elementos que aparecen desde arriba */
@keyframes slideDown

opacity: 1;
    transform: translateY(0);
  }
}

/* Step Slide In - Para pasos de gu铆a */
@keyframes stepSlideIn

opacity: 1;
    transform: translateX(0);
  }
}

/* ==========================================
   FASE 4: ANIMACIONES BASE - ENTRADA
   ========================================== */

/* Slide Up - Aparece desde abajo */
@keyframes slideUp

transform: translateY(0);
    opacity: 1;
  }
}

/* Scale In - Aparece con escala */
@keyframes scaleIn

transform: scale(1);
    opacity: 1;
  }
}

/* Stagger In - Para listas con delay */
@keyframes staggerIn

animation: scaleIn var(--duration-normal) var(--ease-spring);
  animation-fill-mode: both;
}

.stagger-in

animation: staggerIn var(--duration-normal) var(--ease-out-expo);
  animation-fill-mode: both;
}

/* Fade In mejorado */
.fade-in

